import{pokemonColorMap,dataPokemons,getPokemonImageUri}from"./utils.js";const container=document.querySelector(".all-container");function renderTopPokemons(){dataPokemons.results.forEach(createPokemonContainer)}async function createPokemonContainer(t,o){var e=await getPokemonData(t.url),a=(console.log(e),formatAbilities(e.abilities)),e=formatStats(e.stats),t=new PokemonCard(getPokemonImageUri(o+1),t.name,pokemonColorMap[o+1],{abilitiesHTML:a,statsHTML:e});container.appendChild(t)}async function getPokemonData(t){return fetch(t).then(t=>t.json())}function formatAbilities(t){let o="<p>Abilities</p>";return o+="<ul>",t.forEach(t=>{o+=`<li class='ability'>${t.ability.name}</li>`}),o+="</ul>"}function formatStats(t){let o="<p>Stats</p>";return o+="<ul>",t.forEach(t=>{o+=`<li class='ability'><strong>${t.stat.name}:</strong> ${t.base_stat}</li>`}),o+="</ul>"}renderTopPokemons();